import React, { useState, useEffect } from "react";
import './styles.css';
import { Link, useNavigate } from 'react-router-dom';
import { FiEdit, FiUserX, FiXCircle } from "react-icons/fi";
import api from '../../services/api';

export default function Alunos(){

    const[alunos, setAlunos] = useState([]);

    cosnt

    const email = localStorage.getItem('email');
    const token = localStorage.getItem('token');

    const history = useNavigate();

    const authorization = {
        headers : {
            Authorization : `Bearer ${token}`
        }
    }

    useEffect (()=>{
        api.get('/api/alunos', authorization).then(
            response => {setAlunos(response.data);
        }, token)
    })

    async function logout(){
        try {
            localStorage.clear();
            localStorage.setItem('token', '');
            authorization.headers = '';
            history('/');         
        } catch (err) {
            alert('Não foi possivel fazer o logout ' + err);
        }
    }

    useEffect(()=>{
        if(alunoId === '0')
            return;
        else
        loadAluno();
    }, [alunoId])

    async function loadAluno(){
        try {

            const response = await api.get(`api/Alunos/${alunoId}`, authorization);

            setId(response.data.id);
            setNome(response.data.nome);
            setEmail(response.data.email);
            setIdade(response.data.idade);

        } catch (error) {

            alert('Erro ao recuperar o aluno ' + error);
            history('/alunos');

        }
    }

    async function editAluno(id){
        try {
            history(`/aluno/novo/${id}`)
        } catch (error) {
            alert('Não foi possivel editar aluno')
        }
    }

    return(
        <div className="aluno-container">
            <header>
                <span>Bem vindo, <strong>{email}</strong>!</span>
            <Link className="button" to="/aluno/novo/0">Novo Aluno</Link>
            <button type="button" onClick={logout}>
                <FiXCircle size={35} color="#17202a"/>
            </button>
            </header>
            <form>
                <input type="text" placeholder="Nome"/>
                <button type="button" class='button'>
                    Filtar aluno por nome(parcial)
                </button>
            </form>
            <h1>Relação de aluno</h1>
            <ul>
                {alunos.map(aluno=>(
                    <li key={aluno.id}>
                        <b>Nome: </b>{aluno.nome} <br /><br />
                        <b>Email: </b>{aluno.email} <br /><br />
                        <b>Idade: </b>{aluno.nome} <br /><br />

                    <button onClick={()=>editAluno(aluno.id)} type="button">
                        <FiEdit size="25"  color="#17202a"/>                       
                    </button>
                    <button type="button">
                        <FiUserX size="25"  color="#17202a"/>                       
                    </button>
                </li>
                ))}
            </ul>
        </div>
    );
}
